<style>
  nav {
    background-color: var(--medium);
    border-right: 1px solid var(--light-yellow);
  }

  ul {
    display: flex;
    flex-direction: column;
    width: 100%;
  }

  li {
    padding: 1rem 0;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
  }

  li:hover::before {
    content: attr(data-text);
    position: absolute;
    left: 114%;
    top: 50%;
    transform: translateY(-50%);
    background-color: var(--yellow);
    color: var(--darkest);
    font-size: 0.875rem;
    padding: 0.3rem 0.8rem;
    font-weight: bold;
    border-radius: 5px;
    z-index: 2;
    white-space: nowrap;
  }

  li:hover::after {
    content: "";
    position: absolute;
    right: -20px;
    top: 50%;
    background-color: var(--yellow);
    height: 0.8rem;
    width: 0.8rem;
    transform: translateY(-50%) rotateZ(45deg);
    z-index: 1;
  }

  li.active {
    background-color: var(--yellow) !important;
  }

  li.active svg {
    fill: var(--darkest);
  }

  li:hover {
    background-color: var(--darkest);
    cursor: pointer;
  }
</style>